import { Card } from "react-bootstrap";
import Btn from '../Button.js';
import Form from 'react-bootstrap/Form';


function LoginForm() {
    const cardStyle = {
        backgroundColor: '#D9D9D9',
        boxShadow: 'rgba(99, 99, 99, 0.2) 0px 2px 8px 0px',
        borderRadius: '64px',
        border: 'none',
        padding: '3%',
        width: '30%',
        height: 'fit-content',
        margin: '0 auto',
        textAlign: 'center',
    };
    
    const inputStyle = {
        focus: 'none',
        outline: 'none',
        border: 'solid 1px #252525',
        backgroundColor: 'transparent',
        borderRadius: '64px',
        margin: '5% 0 5% 0',
    };

    return (
        <Card style={cardStyle}>
            <h3>Loving Devs</h3>
            <Form>
                <Form.Select style={inputStyle} aria-label="Default select example">
                    <option>Quem é você?</option>
                    <option value="1">Lulu</option>
                    <option value="2">Guizen</option>
                </Form.Select>
                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Control style={inputStyle} type="password" placeholder="Senha" />
                </Form.Group>
                <Btn name={'Login'}/>
            </Form>
        </Card>
    )
}

export default LoginForm;
